def cat_sort(cat: str):
    categories = {
      "hygiene"    : 1,
      "drinks"     : 2,
      "produce"    : 3,
      "baking"     : 4,
      "condiments" : 5,
      "oils"       : 6,
      "cans"       : 7,
      "staples"    : 8,
      "milk"       : 9,
      "breakfast"  : 10,
      "snacks"     : 11,
      "nometro"    : 100,
    }
    if cat in categories:
        return categories[cat]
    else:
        return 0
